    override func viewDidLoad() {
        super.viewDidLoad()
        let request = GADRequest()
        request.testDevices = [kGADSimulatorID]
        bannerView.adUnitID = "ca-app-pub-3940256099942544/2934735716"
        bannerView.rootViewController = self
        bannerView.load(request)
        
        
        
        
        
        nextLbl.applyDesign2()
        prelbl.applyDesign2()
        homelbl.applyDesign2()
        hildelbl.applyDesign2()
        senlbl.applyDesign2()
        playlbl.applyDesign2()
       
        enLabel.text = enWords[timesClicked]
        porLabel.text = portugueseWords[timesClicked]
        nepaliLabel.text = nepaliWords[timesClicked]
        updateScore()
        prelbl.isEnabled = false
       
    }

   
    @IBAction func btnsClicked(_ sender: UIButton) {
        switch sender {
        case hildelbl:
            
            porLabel.isHidden = true
        case senlbl:
            enLabel.text = enSentence[timesClicked]
            porLabel.text = ptSentence[timesClicked]
            nepaliLabel.text = nepaliWords[timesClicked]
            porLabel.isHidden = false
            enLabel.isHidden = false
        default:
            break
            
        }
    }
    
    
    
    
    
    @IBAction func next1(_ sender: UIButton) {
        let numberNotInArray = timesClicked + 1
        if (numberNotInArray < enWords.count){
           timesClicked = timesClicked + 1
            prelbl.isEnabled = true
            
                
            enLabel.text = enWords[timesClicked]
            porLabel.text = portugueseWords[timesClicked]
            nepaliLabel.text = nepaliWords[timesClicked]
                updateScore()
            }
            
        else{
            prelbl.isEnabled = false
            timesClicked = 0
            enLabel.text = enWords[timesClicked]
            porLabel.text = portugueseWords[timesClicked]
            nepaliLabel.text = nepaliWords[timesClicked]
            updateScore()
            
            
        }
    }

    @IBAction func preBtnPressed(_ sender: UIButton) {
        let numberNotInArray = timesClicked - 1
        if (numberNotInArray < enWords.count){
            timesClicked = timesClicked - 1
            
            enLabel.text = enWords[timesClicked]
            porLabel.text = portugueseWords[timesClicked]
            nepaliLabel.text = nepaliWords[timesClicked]
            
            updateScore()
            if (numberNotInArray==timesClicked){
                prelbl.isEnabled = false
                updateScore()
            }
        
            
        }
        else{
            
           
            prelbl.isEnabled = false
             timesClicked = 0
            updateScore()
        }
        
        
        
        
    }
    @IBAction func back2(_ sender: UIButton) {
        self.dismiss(animated: true, completion: nil)
    }
    func updateScore(){
        scLabel.text = "\(timesClicked)" + "/" + " \(enWords.count)"
    }
    @IBAction func playClicked(_ sender: Any) {
        myUtterance = AVSpeechUtterance(string:(porLabel.text)!)
        myUtterance.voice = AVSpeechSynthesisVoice(language: "pt-PT")
        myUtterance.rate = 0.3
        synth.speak(myUtterance)
    }
}


